<style lang="scss">
	.container {
		background-color: var(--app-color-navy);
		color: var(--app-color-navy-contrast);
		display: flex;
		flex-direction: column;
		justify-content: end;
		height: 100vh;
		padding: 4rem 16rem 4rem 16rem;
		gap: 4vw;
	}
	.categories {
		display: flex;
		flex-direction: column;
		font-family: 'Satoshi';
		font-style: normal;
		font-weight: normal;
        font-size: 12px;
        line-height: 16px;
		text-transform: uppercase;
        gap: 0.5vw;
	}
    .heading {
        max-height: 80px;
    }
	.subheading {
		font-family: 'Bebas Neue';
		font-style: normal;
		font-weight: normal;
		font-size: 24px;
        line-height: 110%;
        max-width: 690px;
	}

	.adtagscontainer {
		display: flex;
        // grid-template-columns: 1fr 1fr;
        // grid-template-rows: 1fr 1fr;
		flex-direction: row;
		flex-wrap: wrap;
		justify-content: space-between;
		gap: 8vw;

		.adtags {
			display: flex;
			flex-direction: column;
			gap: 4vw;
			width: 128px;

			.adtaginfo {
				font-family: 'Satoshi';
				font-style: normal;
				font-weight: normal;
				font-size: 12px;
				line-height: 140%;
				color: var(--app-color-blue);
			}
		}
	}
    .hidden-lg {
        display: none;
    }

    @media(max-width: 425px) {
        .container {
            padding: 4rem 2rem 4rem 2rem;
            gap: 6vw;
        }
        .categories {
            font-size: 12px;
		    line-height: 16px;
            gap: 1vw;
        }
        .subheading {
            font-size: 20px;
		    line-height: 110%;
        }
        .hidden-lg {
            display: block;
        }
    }
</style>
